export class LifeCycleDemo implements OnInit, OnChanges, AfterViewInit, OnDestroy {

  // 1. Triggered when @Input values change
  ngOnChanges(changes: SimpleChanges) {
    console.log('Inputs changed', changes);
  }

  // 2. Triggered once after component initialization
  // BEST PLACE for API calls.
  ngOnInit() {
    this.loadData();
  }

  // 3. Triggered after the HTML template is fully rendered
  ngAfterViewInit() {
    // Access DOM elements here (e.g., Canvas, Scroll position)
  }

  // 4. Triggered just before component is removed
  // BEST PLACE for cleanup (unsubscribing).
  ngOnDestroy() {
    this.stream$.unsubscribe();
  }
}

@Injectable({ providedIn: 'root' })
export class AuthService {
  // BehaviorSubject holds the current value and emits to new subscribers
  private currentUser = new BehaviorSubject<User | null>(null);

  // Expose as Observable (read-only) for components
  user$ = this.currentUser.asObservable();

  login(credentials: any) {
    // Logic here...
  }
}

export const authInterceptor: HttpInterceptorFn = (req, next) => {
  const token = localStorage.getItem('token');

  if (token) {
    const cloned = req.clone({
      setHeaders: { Authorization: `Bearer ${token}` }
    });
    return next(cloned);
  }

  return next(req);
};

// Route Definition
export const routes: Routes = [
  {
    path: 'dashboard',
    loadComponent: () => import('./dashboard.component').then(m => m.DashboardComponent),
    canActivate: [authGuard]
  }
];

// Functional Guard
export const authGuard: CanActivateFn = (route, state) => {
  const authService = inject(AuthService);
  const router = inject(Router);

  if (authService.isLoggedIn()) {
    return true;
  }

  return router.createUrlTree(['/login']);
};
